    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        double fahrenheit = Double.parseDouble(reamurField.getText())*2.25 + 32;
        fahrenheitField.setText(fahrenheit+ "");
        double celcius = Double.parseDouble(reamurField.getText())/0.8;
        celciusField.setText(celcius+ "");
        double kelvin = Double.parseDouble(reamurField.getText())/0.8 + 273.15;
        kelvinField.setText(kelvin+ "");
    }//GEN-LAST:event_jButton1ActionPerformed

